import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getCategory } from '../api/api'; // Updated import to match api.js
import './Home.css';

function Home() {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const categoriesData = [
          { name: 'Trending', categoryId: 'trending' },
          { name: 'Action', categoryId: 'action' },
          { name: 'Comedy', categoryId: 'comedy' },
          { name: 'Drama', categoryId: 'drama' },
          { name: 'Horror', categoryId: 'horror' },
        ];
        const updatedCategories = await Promise.all(
          categoriesData.map(async (category) => {
            const response = await getCategory(category.categoryId);
            return { ...category, movies: response.data };
          })
        );
        setCategories(updatedCategories);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching categories:', err);
        setError('Failed to load categories. Please try again later.');
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="home-container">
      <header className="header">
        <h1 className="logo">MovieVerse 2.0</h1>
      </header>
      <div className="categories">
        {categories.map((category) => (
          <div key={category.categoryId} className="category-section">
            <h2>{category.name}</h2>
            <div className="movie-grid">
              {category.movies.map((movie) => (
                <div key={movie.externalId} className="movie-card">
                  <div className="movie-poster">
                    <img src={movie.poster || 'https://placehold.co/200x300?text=No+Poster'} alt={movie.title} />
                  </div>
                  <h3>{movie.title}</h3>
                  <Link to={`/movies/${movie.source}/${movie.externalId}`} className="view-details">
                    View Details
                  </Link>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <footer className="footer">
        <p>Â© 2025 MovieVerse. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default Home;
